% Chapter 9: Figure 9.51%%   Remotely controlled vehicle system Nyquist chart %   for K= 10 with automatic labeling of gain and%   phase margin.%numgc=10*[1 2]; dengc=[1 1]; sysgc=tf(numgc,dengc);numg=[1]; deng=[1 2 4]; sysg=tf(numg,deng);%sys=series(sysgc,sysg);%[Gm,Pm,Wcg,Wcp]=margin(sys);%nyquist(sys);title(['Gm = ',num2str(Gm),'  Pm = ',num2str(Pm)])